mapper.select:
select t1.addtime, order_total, total_price, coupon_id, coupon_price, freight_price, pay_way, addr_realname, addr_phone, addr_city, address, ip_address, user_ip, t1.order_id, status, t1.user_id, t2.username, t2.phone, express_title, express_name, express_num from shop_order t1 left join shop_user t2 on t1.user_id=t2.user_id where 1=1
and ( username like concat(concat('%',#{pd.username}),'%') or phone like concat(concat('%',#{pd.username}),'%') )
and (SELECT COUNT(1) from shop_order_detail sd WHERE status=#{pd.status} and sd.order_id=t1.order_id)>0
and order_id=#{pd.order_id}
and t1.user_id=#{pd.user_id}
order by t1.addtime desc

select t1.addtime, order_total, total_price, coupon_id, coupon_price,freight_price, pay_way, addr_realname, addr_phone, addr_city, address, ip_address, user_ip, t1.order_id, status, t1.user_id, t2.username, t2.phone, express_title, express_name, express_num from shop_order t1 left join shop_user t2 on t1.user_id=t2.user_id where 1=1
and ( username like concat(concat('%',#{username}),'%') or phone like concat(concat('%',#{username}),'%') )
and status=#{status}
and order_id=#{order_id}
and t1.user_id=#{user_id}
order by t1.addtime desc

select
count(1)
from shop_order
where 1=1

and status=#{status}


and user_id=#{user_id}
